INSERT INTO #__sections VALUES (1, 'Σύντομα νέα', 'Σύντομα νέα', NULL, 'content', 'left', '<p>Αυτή η ενότητα και οι κατηγορίες της δεν συνδέονται με κανένα μενού και γιαυτό είναι άμεσα εμφανείς στο δημόσιο τμήμα του ιστότοπου. Τα αντικείμενα περιεχομένου αυτής της ενότητας και των κατηγοριών της χρησιμοποιούνται μόνο για την τροφοδοσία του module Newsflash. <u>Δείτε επίσης</u>, MODULES-&gt;MODULES ΙΣΤΟΤΟΠΟΥ-&gt;NEWSFLASH</p>', 1, 0, '1979-12-19 00:00:00', 1, 29, 0, NULL, NULL, 'newsflashes');
INSERT INTO #__sections VALUES (2, 'Συχνές ερωτήσεις', 'Συχνές ερωτήσεις', 'pastarchives.jpg', 'content', 'left', '<p>Αυτή η ενότητα περιέχει απαντήσεις σε Συχνές Ερωτήσεις αναφορικά με το Elxis και τις λειτουργίες του.</p>', 1, 0, '1979-12-19 00:00:00', 2, 29, 0, NULL, NULL, 'faqs');
INSERT INTO #__sections VALUES (3, 'Ανακαλύψτε το Elxis 2009', 'Ανακαλύψτε το Elxis 2009', NULL, 'content', 'left', '<p align="justify">Το Elxis εξελίχθηκε! Αυτή η ενότητα παρουσιάζει τα σημαντικότερα χαρακτηριστικά, προσθήκες και αλλαγές του Elxis 2009.</p>', 1, 0, '1979-12-19 00:00:00', 3, 29, 0, NULL, NULL, 'discover-elxis');
INSERT INTO #__categories VALUES (1, 0, 'Newsflash', 'Newsflash', NULL, '1', 'left', '<p>Αυτή η ενότητα και οι κατηγορίες της δεν συνδέονται με κανένα μενού και γιαυτό είναι άμεσα εμφανείς στο δημόσιο τμήμα του ιστότοπου. Τα αντικείμενα περιεχομένου αυτής της ενότητας και των κατηγοριών της χρησιμοποιούνται μόνο για την τροφοδοσία του module Newsflash. <u>Δείτε επίσης</u>, MODULES-&gt;MODULES ΙΣΤΟΤΟΠΟΥ-&gt;NEWSFLASH</p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, NULL, 'newsflash');
